client/src/pages/products.tsx.content:
import { useState, useEffect } from "react";
import { useAuth } from "@/hooks/useAuth";
import { useToast } from "@/hooks/use-toast";
import { useQuery, useMutation } from "@tanstack/react-query";
import { queryClient, apiRequest } from "@/lib/queryClient";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { z } from "zod";
import Sidebar from "@/components/layout/sidebar";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Dialog, DialogContent, DialogDescription, DialogHeader, DialogTitle, DialogTrigger } from "@/components/ui/dialog";
import { Form, FormControl, FormField, FormItem, FormLabel, FormMessage } from "@/components/ui/form";
import { Package, Plus, Search, Edit, Trash2, AlertTriangle } from "lucide-react";
import { isUnauthorizedError } from "@/lib/authUtils";

const productSchema = z.object({
  name: z.string().min(1, "El nombre es requerido"),
  barcode: z.string().optional(),
  category: z.string().min(1, "La categoría es requerida"),
  costPrice: z.string().min(1, "El precio de compra es requerido"),
  salePrice: z.string().min(1, "El precio de venta es requerido"),
  currentStock: z.string().min(1, "El stock actual es requerido"),
  minStock: z.string().min(1, "El stock mínimo es requerido"),
});

type ProductFormData = z.infer<typeof productSchema>;

// Product Filters Dialog Component
function ProductFiltersDialog({ 
  filters, 
  onFiltersChange, 
  onClose 
}: { 
  filters: any; 
  onFiltersChange: (filters: any) => void; 
  onClose: () => void; 
}) {
  const handleFilterChange = (key: string, value: any) => {
    onFiltersChange({ ...filters, [key]: value });
  };

  const clearAllFilters = () => {
    onFiltersChange({
      category: "",
      minPrice: "",
      maxPrice: "",
      stockLevel: "",
      showLowStockOnly: false
    });
  };

  const hasActiveFilters = Object.values(filters).some(v => v !== "" && v !== false);

  return (
    <DialogContent className="sm:max-w-[500px]" data-testid="dialog-product-filters">
      <DialogHeader>
        <DialogTitle>Filtros de Productos</DialogTitle>
        <DialogDescription>
          Filtra los productos por categoría, precio, y nivel de stock
        </DialogDescription>
      </DialogHeader>

      <div className="space-y-4">
        {/* Category Filter */}
        <div className="space-y-2">
          <Label htmlFor="category-filter">Categoría</Label>
          <Input
            id="category-filter"
            placeholder="Ej: Bebidas, Golosinas..."
            value={filters.category}
            onChange={(e) => handleFilterChange("category", e.target.value)}
            data-testid="input-filter-category"
          />
        </div>

        {/* Price Range Filter */}
        <div className="grid grid-cols-2 gap-4">
          <div className="space-y-2">
            <Label htmlFor="min-price">Precio Mínimo</Label>
            <Input
              id="min-price"
              type="number"
              placeholder="0.00"
              value={filters.minPrice}
              onChange={(e) => handleFilterChange("minPrice", e.target.value)}
              data-testid="input-filter-min-price"
            />
          </div>
          <div className="space-y-2">
            <Label htmlFor="max-price">Precio Máximo</Label>
            <Input
              id="max-price"
              type="number"
              placeholder="999.99"
              value={filters.maxPrice}
              onChange={(e) => handleFilterChange("maxPrice", e.target.value)}
              data-testid="input-filter-max-price"
            />
          </div>
        </div>

        {/* Stock Level Filter */}
        <div className="space-y-2">
          <Label>Nivel de Stock</Label>
          <Select
            value={filters.stockLevel}
            onValueChange={(value) => handleFilterChange("stockLevel", value)}
          >
            <SelectTrigger data-testid="select-stock-level">
              <SelectValue placeholder="Todos los niveles" />
            </SelectTrigger>
            <SelectContent>
              <SelectItem value="all">Todos los niveles</SelectItem>
              <SelectItem value="low">Stock Bajo (≤ mínimo)</SelectItem>
              <SelectItem value="normal">Stock Normal</SelectItem>
              <SelectItem value="high">Stock Alto</SelectItem>
            </SelectContent>
          </Select>
        </div>

        {/* Low Stock Only Toggle */}
        <div className="flex items-center space-x-2">
          <input
            type="checkbox"
            id="low-stock-only"
            checked={filters.showLowStockOnly}
            onChange={(e) => handleFilterChange("showLowStockOnly", e.target.checked)}
            className="rounded border-gray-300"
            data-testid="checkbox-low-stock-only"
          />
          <Label htmlFor="low-stock-only" className="text-sm">
            Mostrar solo productos con stock bajo
          </Label>
        </div>
      </div>

      <div className="flex justify-between pt-4">
        <Button 
          variant="outline" 
          onClick={clearAllFilters}
          disabled={!hasActiveFilters}
          data-testid="button-clear-filters"
        >
          Limpiar Filtros
        </Button>
        <div className="space-x-2">
          <Button 
            variant="outline" 
            onClick={onClose}
            data-testid="button-cancel-filters"
          >
            Cancelar
          </Button>
          <Button 
            onClick={onClose}
            data-testid="button-apply-filters"
          >
            Aplicar Filtros
          </Button>
        </div>
      </div>
    </DialogContent>
  );
}

export default function Products() {
  const { toast } = useToast();
  const { isAuthenticated, isLoading } = useAuth();
  const [isDialogOpen, setIsDialogOpen] = useState(false);
  const [searchTerm, setSearchTerm] = useState("");
  const [isFilterDialogOpen, setIsFilterDialogOpen] = useState(false);
  const [editingProduct, setEditingProduct] = useState<any>(null);
  const [filters, setFilters] = useState({
    category: "",
    minPrice: "",
    maxPrice: "",
    stockLevel: "all", // all, low, normal, high
    showLowStockOnly: false
  });

  // Load products
  const { data: products, isLoading: productsLoading, error: productsError } = useQuery({
    queryKey: ["/api/products"],
    enabled: isAuthenticated,
  });

  // Load low stock products
  const { data: lowStockProducts, isLoading: lowStockLoading } = useQuery({
    queryKey: ["/api/dashboard/low-stock"],
    enabled: isAuthenticated,
  });

  const filteredProducts = products ? (products as any[]).filter((product: any) => {
    // Basic search filter
    const matchesSearch = product.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
      (product.barcode && product.barcode.includes(searchTerm));

    // Category filter
    const matchesCategory = !filters.category || 
      (product.category && product.category.toLowerCase().includes(filters.category.toLowerCase()));

    // Price range filter
    const salePrice = parseFloat(product.salePrice);
    const matchesMinPrice = !filters.minPrice || salePrice >= parseFloat(filters.minPrice);
    const matchesMaxPrice = !filters.maxPrice || salePrice <= parseFloat(filters.maxPrice);

    // Stock level filter
    const currentStock = parseInt(product.currentStock);
    const minStock = parseInt(product.minStock);
    let matchesStockLevel = true;

    if (filters.stockLevel === "low") {
      matchesStockLevel = currentStock <= minStock;
    } else if (filters.stockLevel === "normal") {
      matchesStockLevel = currentStock > minStock && currentStock <= minStock * 2;
    } else if (filters.stockLevel === "high") {
      matchesStockLevel = currentStock > minStock * 2;
    } else if (filters.stockLevel === "all" || !filters.stockLevel) {
      matchesStockLevel = true;
    }

    // Low stock only filter
    const matchesLowStockOnly = !filters.showLowStockOnly || currentStock <= minStock;

    return matchesSearch && matchesCategory && matchesMinPrice && 
           matchesMaxPrice && matchesStockLevel && matchesLowStockOnly;
  }) : [];

  // Mutations for product management
  const deleteProductMutation = useMutation({
    mutationFn: async (productId: string) => {
      return await apiRequest("DELETE", `/api/products/${productId}`);
    },
    onSuccess: () => {
      toast({
        title: "Producto eliminado",
        description: "El producto se ha eliminado exitosamente.",
      });
      queryClient.invalidateQueries({ queryKey: ["/api/products"] });
      queryClient.invalidateQueries({ queryKey: ["/api/dashboard/low-stock"] });
    },
    onError: (error: any) => {
      toast({
        title: "Error",
        description: "Error al eliminar el producto",
        variant: "destructive",
      });
    },
  });

  // Handler functions
  const handleEdit = (product: any) => {
    setEditingProduct(product);
    setIsDialogOpen(true);
  };

  const handleDelete = (productId: string) => {
    if (confirm('¿Estás seguro de que deseas eliminar este producto?')) {
      deleteProductMutation.mutate(productId);
    }
  };

  // Redirect to home if not authenticated
  useEffect(() => {
    if (!isLoading && !isAuthenticated) {
      toast({
        title: "No autorizado",
        description: "Debes iniciar sesión para acceder al sistema.",
        variant: "destructive",
      });
      setTimeout(() => {
        window.location.href = "/api/login";
      }, 500);
      return;
    }
  }, [isAuthenticated, isLoading, toast]);

  if (isLoading || !isAuthenticated) {
    return (
      <div className="min-h-screen flex items-center justify-center">
        <div className="text-center">
          <div className="w-8 h-8 border-2 border-primary border-t-transparent rounded-full animate-spin mx-auto mb-4"></div>
          <p className="text-muted-foreground">Cargando...</p>
        </div>
      </div>
    );
  }

  return (
    <div className="flex h-screen bg-background">
      <Sidebar />

      <main className="flex-1 overflow-auto">
        <div className="border-b bg-card p-6">
          <div className="flex items-center justify-between">
            <div>
              <h2 className="text-2xl font-bold text-foreground">Gestión de Productos</h2>
              <p className="text-muted-foreground">Administra tu inventario y control de stock</p>
            </div>
            <Dialog open={isDialogOpen} onOpenChange={setIsDialogOpen}>
              <DialogTrigger asChild>
                <Button className="flex items-center space-x-2" data-testid="button-new-product">
                  <Plus className="h-4 w-4" />
                  <span>Nuevo Producto</span>
                </Button>
              </DialogTrigger>
              <ProductFormDialog 
                editingProduct={editingProduct} 
                onClose={() => {
                  setIsDialogOpen(false);
                  setEditingProduct(null);
                }} 
              />
            </Dialog>
          </div>
        </div>

        <div className="p-6 space-y-6">
          {/* Búsqueda y Filtros */}
          <Card>
            <CardContent className="pt-6">
              <div className="flex items-center space-x-4">
                <div className="relative flex-1">
                  <Search className="absolute left-3 top-2.5 h-4 w-4 text-muted-foreground" />
                  <Input 
                    placeholder="Buscar productos por nombre o código..." 
                    className="pl-10"
                    value={searchTerm}
                    onChange={(e) => setSearchTerm(e.target.value)}
                    data-testid="input-search-products"
                  />
                </div>
                <Dialog open={isFilterDialogOpen} onOpenChange={setIsFilterDialogOpen}>
                  <DialogTrigger asChild>
                    <Button variant="outline" data-testid="button-filter">
                      Filtros {Object.values(filters).some(v => v !== "" && v !== false) && (
                        <span className="ml-1 px-1.5 py-0.5 text-xs bg-primary text-primary-foreground rounded-full">
                          {Object.values(filters).filter(v => v !== "" && v !== false).length}
                        </span>
                      )}
                    </Button>
                  </DialogTrigger>
                  <ProductFiltersDialog 
                    filters={filters}
                    onFiltersChange={setFilters}
                    onClose={() => setIsFilterDialogOpen(false)}
                  />
                </Dialog>
                <Button variant="outline" data-testid="button-import">
                  Importar
                </Button>
              </div>
            </CardContent>
          </Card>

          {/* Alertas de Stock */}
          <Card>
            <CardHeader>
              <CardTitle className="flex items-center space-x-2 text-orange-600">
                <AlertTriangle className="h-5 w-5" />
                <span>Productos con Stock Bajo</span>
              </CardTitle>
            </CardHeader>
            <CardContent>
              <div className="space-y-3">
                {lowStockLoading ? (
                  <div className="text-center py-4 text-muted-foreground">
                    <Package className="h-8 w-8 mx-auto mb-2 opacity-50" />
                    <p className="text-sm">Cargando alertas de stock...</p>
                  </div>
                ) : lowStockProducts && (lowStockProducts as any[]).length > 0 ? (
                  (lowStockProducts as any[]).map((product: any) => (
                    <div key={product.id} className="flex items-center justify-between p-3 bg-orange-50 dark:bg-orange-950 rounded-lg border border-orange-200 dark:border-orange-800">
                      <div>
                        <p className="font-medium text-sm">{product.name}</p>
                        <p className="text-xs text-muted-foreground">Stock: {product.currentStock} (Mínimo: {product.minStock})</p>
                      </div>
                      <AlertTriangle className="h-4 w-4 text-orange-600" />
                    </div>
                  ))
                ) : (
                  <div className="text-center py-4 text-muted-foreground">
                    <Package className="h-8 w-8 mx-auto mb-2 opacity-50" />
                    <p className="text-sm">No hay productos con stock bajo.</p>
                  </div>
                )}
              </div>
            </CardContent>
          </Card>

          {/* Lista de Productos */}
          <Card>
            <CardHeader>
              <CardTitle className="flex items-center space-x-2">
                <Package className="h-5 w-5" />
                <span>Todos los Productos</span>
              </CardTitle>
            </CardHeader>
            <CardContent>
              <div className="space-y-4">
                {productsLoading ? (
                  <div className="text-center py-8 text-muted-foreground">
                    <Package className="h-12 w-12 mx-auto mb-4 opacity-50" />
                    <p>Cargando productos...</p>
                  </div>
                ) : productsError ? (
                  <div className="text-center py-8 text-red-500">
                    <Package className="h-12 w-12 mx-auto mb-4 opacity-50" />
                    <p>Error al cargar productos</p>
                    <p className="text-sm">{String(productsError)}</p>
                  </div>
                ) : !filteredProducts.length ? (
                  <div className="text-center py-8 text-muted-foreground">
                    <Package className="h-12 w-12 mx-auto mb-4 opacity-50" />
                    <p>No hay productos registrados aún.</p>
                    <p className="text-sm">Comienza agregando tu primer producto.</p>
                    <Dialog open={isDialogOpen} onOpenChange={setIsDialogOpen}>
                      <DialogTrigger asChild>
                        <Button className="mt-4" data-testid="button-add-first-product">
                          <Plus className="h-4 w-4 mr-2" />
                          Agregar Primer Producto
                        </Button>
                      </DialogTrigger>
                      <ProductFormDialog 
                        editingProduct={editingProduct} 
                        onClose={() => {
                          setIsDialogOpen(false);
                          setEditingProduct(null);
                        }} 
                      />
                    </Dialog>
                  </div>
                ) : (
                  <div className="space-y-4">
                    {filteredProducts.map((product: any) => (
                      <div key={product.id} className="border rounded-lg p-4" data-testid={`product-item-${product.id}`}>
                        <div className="flex items-center justify-between">
                          <div className="flex-1">
                            <h3 className="font-medium text-sm" data-testid={`product-name-${product.id}`}>{product.name}</h3>
                            <div className="text-xs text-muted-foreground space-y-1 mt-1">
                              {product.barcode && (
                                <p data-testid={`product-barcode-${product.id}`}>Código: {product.barcode}</p>
                              )}
                              <p data-testid={`product-stock-${product.id}`}>Stock: {product.currentStock}</p>
                            </div>
                          </div>
                          <div className="text-right">
                            <p className="font-medium text-sm" data-testid={`product-price-${product.id}`}>${product.salePrice}</p>
                            <p className="text-xs text-muted-foreground">Costo: ${product.costPrice || 'N/A'}</p>
                          </div>
                          <div className="ml-4 flex space-x-2">
                            <Button 
                              variant="outline" 
                              size="sm" 
                              onClick={() => handleEdit(product)}
                              data-testid={`button-edit-product-${product.id}`}
                            >
                              <Edit className="h-3 w-3" />
                            </Button>
                            <Button 
                              variant="outline" 
                              size="sm" 
                              onClick={() => handleDelete(product.id)}
                              data-testid={`button-delete-product-${product.id}`}
                            >
                              <Trash2 className="h-3 w-3" />
                            </Button>
                          </div>
                        </div>
                      </div>
                    ))}
                  </div>
                )}
              </div>
            </CardContent>
          </Card>
        </div>
      </main>
    </div>
  );
}

function ProductFormDialog({ editingProduct, onClose }: { editingProduct?: any; onClose: () => void }) {
  const { toast } = useToast();

  const form = useForm<ProductFormData>({
    resolver: zodResolver(productSchema),
    defaultValues: {
      name: editingProduct?.name || "",
      barcode: editingProduct?.barcode || "",
      category: editingProduct?.category || "",
      costPrice: editingProduct?.costPrice?.toString() || "",
      salePrice: editingProduct?.salePrice?.toString() || "",
      currentStock: editingProduct?.currentStock?.toString() || "",
      minStock: editingProduct?.minStock?.toString() || "",
    },
  });

  // Update form when editingProduct changes
  useEffect(() => {
    if (editingProduct) {
      form.reset({
        name: editingProduct.name || "",
        barcode: editingProduct.barcode || "",
        category: editingProduct.category || "",
        costPrice: editingProduct.costPrice?.toString() || "",
        salePrice: editingProduct.salePrice?.toString() || "",
        currentStock: editingProduct.currentStock?.toString() || "",
        minStock: editingProduct.minStock?.toString() || "",
      });
    } else {
      form.reset({
        name: "",
        barcode: "",
        category: "",
        costPrice: "",
        salePrice: "",
        currentStock: "",
        minStock: "",
      });
    }
  }, [editingProduct, form]);

  const createProductMutation = useMutation({
    mutationFn: async (data: ProductFormData) => {
      return await apiRequest("POST", "/api/products", data);
    },
    onSuccess: () => {
      toast({
        title: "Producto creado",
        description: "El producto se ha creado exitosamente.",
      });
      queryClient.invalidateQueries({ queryKey: ["/api/products"] });
      queryClient.invalidateQueries({ queryKey: ["/api/dashboard/low-stock"] });
      form.reset();
      onClose();
    },
    onError: (error: any) => {
      toast({
        title: "Error",
        description: error.message || "Error al crear el producto",
        variant: "destructive",
      });
    },
  });

  const updateProductMutation = useMutation({
    mutationFn: async (data: ProductFormData) => {
      return await apiRequest("PUT", `/api/products/${editingProduct.id}`, data);
    },
    onSuccess: () => {
      toast({
        title: "Producto actualizado",
        description: "El producto se ha actualizado exitosamente.",
      });
      queryClient.invalidateQueries({ queryKey: ["/api/products"] });
      queryClient.invalidateQueries({ queryKey: ["/api/dashboard/low-stock"] });
      form.reset();
      onClose();
    },
    onError: (error: any) => {
      toast({
        title: "Error",
        description: "Error al actualizar el producto",
        variant: "destructive",
      });
    },
  });

  const onSubmit = (data: ProductFormData) => {
    console.log("Form data submitted:", data);
    console.log("Form errors:", form.formState.errors);

    // Transform the data to match backend schema
    const transformedData = {
      name: data.name,
      barcode: data.barcode || undefined,
      category: data.category,
      costPrice: data.costPrice,
      salePrice: data.salePrice,
      currentStock: data.currentStock,
      minStock: data.minStock,
    };

    console.log("Transformed data:", transformedData);

    if (editingProduct) {
      updateProductMutation.mutate(transformedData);
    } else {
      createProductMutation.mutate(transformedData);
    }
  };

  return (
    <DialogContent className="sm:max-w-[500px]" data-testid="dialog-product-form">
      <DialogHeader>
        <DialogTitle>{editingProduct ? 'Editar Producto' : 'Nuevo Producto'}</DialogTitle>
        <DialogDescription>
          {editingProduct ? 'Actualiza la información del producto.' : 'Completa la información del producto para agregarlo al inventario.'}
        </DialogDescription>
      </DialogHeader>

      <Form {...form}>
        <form onSubmit={form.handleSubmit(onSubmit)} className="space-y-4">
          <FormField
            control={form.control}
            name="name"
            render={({ field }) => (
              <FormItem>
                <FormLabel>Nombre del Producto</FormLabel>
                <FormControl>
                  <Input 
                    placeholder="Ej: Coca Cola 500ml" 
                    data-testid="input-product-name"
                    {...field} 
                  />
                </FormControl>
                <FormMessage />
              </FormItem>
            )}
          />

          <FormField
            control={form.control}
            name="barcode"
            render={({ field }) => (
              <FormItem>
                <FormLabel>Código de Barras (Opcional)</FormLabel>
                <FormControl>
                  <Input 
                    placeholder="Ej: 7790001234567" 
                    data-testid="input-product-barcode"
                    {...field} 
                  />
                </FormControl>
                <FormMessage />
              </FormItem>
            )}
          />

          <FormField
            control={form.control}
            name="category"
            render={({ field }) => (
              <FormItem>
                <FormLabel>Categoría</FormLabel>
                <FormControl>
                  <Input 
                    placeholder="Ej: Bebidas, Golosinas, etc." 
                    data-testid="input-product-category"
                    {...field} 
                  />
                </FormControl>
                <FormMessage />
              </FormItem>
            )}
          />

          <div className="grid grid-cols-2 gap-4">
            <FormField
              control={form.control}
              name="costPrice"
              render={({ field }) => (
                <FormItem>
                  <FormLabel>Precio de Compra</FormLabel>
                  <FormControl>
                    <Input 
                      placeholder="150.00" 
                      data-testid="input-product-cost-price"
                      {...field} 
                    />
                  </FormControl>
                  <FormMessage />
                </FormItem>
              )}
            />

            <FormField
              control={form.control}
              name="salePrice"
              render={({ field }) => (
                <FormItem>
                  <FormLabel>Precio de Venta</FormLabel>
                  <FormControl>
                    <Input 
                      placeholder="200.00" 
                      data-testid="input-product-sale-price"
                      {...field} 
                    />
                  </FormControl>
                  <FormMessage />
                </FormItem>
              )}
            />
          </div>

          <div className="grid grid-cols-2 gap-4">
            <FormField
              control={form.control}
              name="currentStock"
              render={({ field }) => (
                <FormItem>
                  <FormLabel>Stock Actual</FormLabel>
                  <FormControl>
                    <Input 
                      placeholder="20" 
                      data-testid="input-product-current-stock"
                      {...field} 
                    />
                  </FormControl>
                  <FormMessage />
                </FormItem>
              )}
            />

            <FormField
              control={form.control}
              name="minStock"
              render={({ field }) => (
                <FormItem>
                  <FormLabel>Stock Mínimo</FormLabel>
                  <FormControl>
                    <Input 
                      placeholder="5" 
                      data-testid="input-product-min-stock"
                      {...field} 
                    />
                  </FormControl>
                  <FormMessage />
                </FormItem>
              )}
            />
          </div>

          <div className="flex justify-end space-x-2 pt-4">
            <Button 
              type="button" 
              variant="outline" 
              onClick={onClose}
              data-testid="button-cancel-product"
            >
              Cancelar
            </Button>
            <Button 
              type="submit" 
              disabled={createProductMutation.isPending || updateProductMutation.isPending}
              data-testid="button-save-product"
            >
              {(createProductMutation.isPending || updateProductMutation.isPending) 
                ? "Guardando..." 
                : editingProduct 
                  ? "Actualizar Producto" 
                  : "Guardar Producto"
              }
            </Button>
          </div>
        </form>
      </Form>
    </DialogContent>
  );
}

